import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

const CarGame = () => {
  const [carPosition, setCarPosition] = useState(200);

  useEffect(() => {
    function handleArrowKey(event) {
      switch(event.keyCode) {
        case 37: // left arrow key
          setCarPosition(carPosition => carPosition - 10);
          break;
        case 39: // right arrow key
          setCarPosition(carPosition => carPosition + 10);
          break;
        default:
          break;
      }
    }

    window.addEventListener('keydown', handleArrowKey);

    return () => window.removeEventListener('keydown', handleArrowKey);
  }, []);

  return (
    <div style={{position: 'relative', height: '200px', background: '#ccc'}}>
      <div style={{position: 'absolute', left: `${carPosition}px`}}>
        ðŸš—
      </div>
    </div>
  )
}

const App = () => {
  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <a href="/">Home</a>
            </li>
            <li>
              <a href="/cargame">CarGame</a>
            </li>
          </ul>
        </nav>

        <Switch>
          <Route exact path="/">
            <p>This is the Home Page</p>
          </Route>
          <Route path="/cargame" component={CarGame} />
        </Switch>
      </div>
    </Router>
  )
};

ReactDOM.render(
  <App />,
  document.getElementById('root')
);
