import React, { useState, useEffect } from "react";

const CarGame = () => {
  const [carPositionX, setCarPositionX] = useState(0);
  const [carPositionY, setCarPositionY] = useState(0);

  useEffect(() => {
    function handleArrowKey(event) {
      switch(event.keyCode) {
        case 37: // left arrow key
          setCarPositionX(carPositionX => Math.max(carPositionX - 10, 0));
          break;
        case 39: // right arrow key
          setCarPositionX(carPositionX => Math.min(carPositionX + 10, window.innerWidth - 60));
          break;
        case 38: // up arrow key
          setCarPositionY(carPositionY => Math.max(carPositionY - 10, 0));
          break;
        case 40: // down arrow key
          setCarPositionY(carPositionY => Math.min(carPositionY + 10, window.innerHeight - 60));
          break;
      }
    }

    window.addEventListener('keydown', handleArrowKey);

    return () => window.removeEventListener('keydown', handleArrowKey);
  }, []);

  return (
    <div className="App" style={{position: 'relative', height: '100vh'}}>
      <div style={{position: 'absolute', left: `${carPositionX}px`, top: `${carPositionY}px`}}>
        ðŸš—
      </div>
    </div>
  )
};

export default CarGame;
