import React, { useState, useEffect } from "react";
import { RoadAsset, CarAsset } from './Assets';

const CarGame = () => {
  const [gameStart, setGameStart] = useState(false);
  const [carPosition, setCarPosition] = useState({x: 250, y: 250});

  useEffect(() => {
    const handleArrowKey = (event) => {
      let newPos = {...carPosition};
      // check if key is not an arrow key to prevent unnecessary render
      if (![37, 38, 39, 40].includes(event.keyCode)) return;

      switch(event.keyCode) {
        case 37: // left arrow
          newPos.x = carPosition.x > 5 ? carPosition.x - 5 : 0;
          break;
        case 39: // right arrow
          newPos.x = carPosition.x < 495 ? carPosition.x + 5 : 500;
          break;
        case 38: // up arrow
          newPos.y = carPosition.y > 5 ? carPosition.y - 5 : 0;
          break;
        case 40: // down arrow
          newPos.y = carPosition.y < 495 ? carPosition.y + 5 : 500;
          break;
      }
      setCarPosition(newPos);
    }

    // only attach event listener when game is started
    if (gameStart) {
      window.addEventListener('keydown', handleArrowKey);
    }

    // remove event listener when game is stopped
    return () => {
      window.removeEventListener('keydown', handleArrowKey);
    }
  }, [carPosition, gameStart]);

  const handleGameStart= () => {
    setGameStart(true);
  }

  return (
    <div className="App" style={{position: 'relative', height: '500px', width: '500px', border: '1px solid'}}>
      <RoadAsset />
      <CarAsset position={carPosition}/>
      {!gameStart && (
        <button style={{position: 'absolute', zIndex: 2}} onClick={handleGameStart}>
          Start Game
        </button>
      )}
    </div>
  );
}

export default CarGame;
