import React, { useState, useEffect, useRef } from 'react';
import { RoadAsset, CarAsset, GoldAsset } from './Assets';

const CarGame = () => {
  const [gameStart, setGameStart] = useState(false);
  const [score, setScore] = useState(0);
  const [goldPosition, setGoldPosition] = useState({x: 250, y: 0});
  // initial car position and direction
  const [carStatus, setCarStatus] = useState({position: {x: 250, y: 250}, direction: null});
  const gameContainer = useRef(null);

  useEffect(() => {
    const handleArrowKey = (event) => {
      // check if key is not an arrow key to prevent unnecessary render
      if (![37, 39].includes(event.keyCode)) return;

      switch(event.keyCode) {
        case 37:
          setCarStatus({ position: carStatus.position, direction: 'left' });
          break;
        case 39:
          setCarStatus({ position: carStatus.position, direction: 'right' });
          break;
      }
    }

    if (gameStart && gameContainer.current) {
      gameContainer.current.focus();
      gameContainer.current.addEventListener('keydown', handleArrowKey);
    }

    return () => {
      if (gameContainer.current) {
        gameContainer.current.removeEventListener('keydown', handleArrowKey);
      }
    }
  }, [carStatus, gameStart]);

  // Car moves left or right every frame
  useEffect(() => {
    const gameInterval = setInterval(() => {
      if (gameStart && carStatus.direction) {
        let newPos = {...carStatus.position};
        switch (carStatus.direction) {
          case 'left':
            if (carStatus.position.x > 0) newPos.x = carStatus.position.x - 5;
            break;
          case 'right':
            if (carStatus.position.x < 500) newPos.x = carStatus.position.x + 5;
            break;
        }
        setCarStatus({ position: newPos, direction: null });
      }
    }, 100);

    return () => {
      clearInterval(gameInterval);
    }
  }, [carStatus, gameStart]);

  // Gold position recalculates and goes ahead of the car every frame
  useEffect(() => {
    const goldInterval = setInterval(() => {
      if (gameStart) {
        let y = goldPosition.y < 480 ? goldPosition.y + 5 : 0;
        let x = y === 0 ? Math.floor(Math.random() * 480) : goldPosition.x;
        setGoldPosition({ x: x, y: y });
      }
    }, 100);

    return () => {
      clearInterval(goldInterval);
    }
  }, [goldPosition, gameStart]);

  // When the car collides with gold
  useEffect(() => {
    if (gameStart) {
      if (Math.abs(carStatus.position.x - goldPosition.x) < 20 &&
          Math.abs(carStatus.position.y - goldPosition.y) < 20) {
        setScore(score => score + 1);
        setGoldPosition({x: Math.floor(Math.random() * 480), y: 0});
      }
    }
  }, [carStatus, gameStart, goldPosition.x, goldPosition.y]);

  return (
    <div
      ref={gameContainer}
      className="App"
      tabIndex={0}
      style={{ position: 'relative', height: '500px', width: '500px', border: '1px solid' }}
    >
      <RoadAsset />
      <CarAsset position={carStatus.position} />
      <GoldAsset position={goldPosition} />
      {!gameStart && (
        <button style={{ position: 'absolute', zIndex: 2 }} onClick={() => setGameStart(true)}>
          Start Game
        </button>
      )}
      {gameStart && (
        <p>
          Score: {score}
        </p>
      )}
    </div>
  );
}

export default CarGame;
