import React, { useState, useEffect, useRef } from 'react';
import { RoadAsset, CarAsset, GoldAsset } from './Assets';

const CarGame = () => {
  const [gameStart, setGameStart] = useState(false);
  const [score, setScore] = useState(0);
  const [goldPosition, setGoldPosition] = useState({x: 100, y: 100});
  // initial car position and direction
  const [carStatus, setCarStatus] = useState({position: {x: 250, y: 250}, direction: null});
  const gameContainer = useRef(null);

  useEffect(() => {
    const handleArrowKey = (event) => {
      // check if key is not an arrow key to prevent unnecessary render
      if (![37, 38, 39, 40].includes(event.keyCode)) return;

      switch(event.keyCode) {
        case 37:
          setCarStatus({ position: carStatus.position, direction: 'left' });
          break;
        case 39:
          setCarStatus({ position: carStatus.position, direction: 'right' });
          break;
        case 38:
          setCarStatus({ position: carStatus.position, direction: 'up' });
          break;
        case 40:
          setCarStatus({ position: carStatus.position, direction: 'down' });
          break;
      }
    }

    if (gameStart && gameContainer.current) {
      gameContainer.current.focus();
      gameContainer.current.addEventListener('keydown', handleArrowKey);
    }

    return () => {
      if (gameContainer.current) {
        gameContainer.current.removeEventListener('keydown', handleArrowKey);
      }
    }

  }, [carStatus, gameStart]);

  // The car keeps moving in its direction every frame
  useEffect(() => {
    const gameInterval = setInterval(() => {
      if (gameStart && carStatus.direction) {
        let newPos = {...carStatus.position};
        switch (carStatus.direction) {
          case 'left':
            newPos.x = carStatus.position.x > 5 ? carStatus.position.x - 5 : stopGame();
            break;
          case 'right':
            newPos.x = carStatus.position.x < 495 ? carStatus.position.x + 5 : stopGame();
            break;
          case 'up':
            newPos.y = carStatus.position.y > 5 ? carStatus.position.y - 5 : stopGame();
            break;
          case 'down':
            newPos.y = carStatus.position.y < 495 ? carStatus.position.y + 5 : stopGame();
            break;
        }
        setCarStatus({ position: newPos, direction: carStatus.direction });
      }
    }, 100);

    return () => {
      clearInterval(gameInterval);
    }
  }, [carStatus, gameStart]);

  const handleGameStart = () => {
    setGameStart(true);
  }

  const stopGame = () => {
    setGameStart(false);
  }

  useEffect(() => {
    if (gameStart) {
      // Check if car's and gold's positions touch
      if (Math.abs(carStatus.position.x - goldPosition.x) < 20 && 
          Math.abs(carStatus.position.y - goldPosition.y) < 20) {
        setScore(score => score + 1);
        setGoldPosition({
          x: Math.floor(Math.random() * 80),
          y: Math.floor(Math.random() * 480)
        });
      }
    }
  }, [carStatus, gameStart, goldPosition.x, goldPosition.y]);

  return (
    <div
      ref={gameContainer}
      className="App"
      tabIndex={0}
      style={{ position: 'relative', height: '500px', width: '500px', border: '1px solid' }}
    >
      <RoadAsset />
      <CarAsset position={carStatus.position} />
      <GoldAsset position={goldPosition} />
      {!gameStart && (
        <button style={{ position: 'absolute', zIndex: 2 }} onClick={handleGameStart}>
          Start Game
        </button>
      )}
      {gameStart && (
        <p>
          Score: {score}
        </p>
      )}
    </div>
  );
}

export default CarGame;
