import React, { useState, useEffect } from "react";

const CarGame = () => {
  const [carPositionX, setCarPositionX] = useState(50);
  const [carPositionY, setCarPositionY] = useState(50);

  useEffect(() => {
    function handleArrowKey(event) {
      if([37, 38, 39, 40].includes(event.keyCode)) {
        event.preventDefault();
      }
      switch(event.keyCode) {
        case 37: // left arrow key
          setCarPositionX(prevPos => prevPos > 10 ? prevPos - 10 : 0);
          break;
        case 39: // right arrow key
          setCarPositionX(prevPos => prevPos < 440 ? prevPos + 10 : 450);
          break;
        case 38: // up arrow key
          setCarPositionY(prevPos => prevPos > 10 ? prevPos - 10 : 0);
          break;
        case 40: // down arrow key
          setCarPositionY(prevPos => prevPos < 440 ? prevPos + 10 : 450);
          break;
        default:
          break;
      }
    }

    window.addEventListener('keydown', handleArrowKey);

    return () => window.removeEventListener('keydown', handleArrowKey);
  }, []);

  return (
    <div className="App" style={{position: 'relative', height: '500px', width: '500px', border: '1px solid'}}>
      <div role="img" aria-label="Car Game" style={{position: 'absolute', left: `${carPositionX}px`, top: `${carPositionY}px`}}>
        ðŸš—
      </div>
    </div>
  )
};

export default CarGame;
