// src/components/Header.js
import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../global.css';
//import Chat from './Chat' //seperate chat
import { useLocation } from 'react-router-dom'; //seperate chat

const Header = () => {
    const location = useLocation(); //seperate chat
    useEffect(() => {
        document.title = "GangBangers";
    }, []);

    const showChat = location.pathname === '/chat'; //seperate chat
    return (
      <>
        <header className="header">
            <nav>
                <ul className="nav-links"> 
                    <li>
                        <Link to="/">Home</Link>
                    </li>
                    <li>
                        <Link to="/login">Login</Link>
                    </li>
                    <li>
                        <Link to="/register">Register</Link>
                    </li>
                    <li>
                        <Link to="/profile">Profile</Link>
                    </li>
                    <li>
                        <Link to="/robbery">Robbery</Link>
                    </li>
                    <li>
                        <Link to="/chat">Chat</Link>
                    </li>
                </ul>
            </nav>
        </header>
      { showChat && <Chat /> }
      </>
    );
};

export default Header;
