import React, { useState, useEffect, useRef } from 'react';
import { RoadAsset, CarAsset } from './Assets';

const CarGame = () => {
  const [gameStart, setGameStart] = useState(false);
  const [score, setScore] = useState(0);
  const [goldPosition, setGoldPosition] = useState({x: 100, y: 100});
  const [carPosition, setCarPosition] = useState({x: 250, y: 250});
  const gameContainer = useRef(null);

  useEffect(() => {
    const handleArrowKey = (event) => {
      let newPos = { ...carPosition };
      // check if key is not an arrow key to prevent unnecessary render
      if (![37, 38, 39, 40].includes(event.keyCode)) return;

      switch(event.keyCode) {
        case 37:
          newPos.x = carPosition.x > 5 ? carPosition.x - 5 : 0;
          break;
        case 39:
          newPos.x = carPosition.x < 495 ? carPosition.x + 5 : 500;
          break;
        case 38:
          newPos.y = carPosition.y > 5 ? carPosition.y - 5 : 0;
          break;
        case 40:
          newPos.y = carPosition.y < 495 ? carPosition.y + 5 : 500;
          break;
      }

      setCarPosition(newPos);
    }

    if (gameStart && gameContainer.current) {
      gameContainer.current.focus();
      gameContainer.current.addEventListener('keydown', handleArrowKey);
    }

    return () => {
      if (gameContainer.current) {
        gameContainer.current.removeEventListener('keydown', handleArrowKey);
      }
    }
  }, [carPosition, gameStart]);

  useEffect(() => {
    if (gameStart) {
      // Check if car's and gold's positions touch
      if (Math.abs(carPosition.x - goldPosition.x) < 20 && 
          Math.abs(carPosition.y - goldPosition.y) < 20) {
        setScore(score => score + 1);
        setGoldPosition({
          x: Math.floor(Math.random() * 480),
          y: Math.floor(Math.random() * 480)
        });
      }
    }
  }, [carPosition, gameStart, goldPosition.x, goldPosition.y]);

  const handleGameStart = () => {
    setGameStart(true);
  }

  return (
    <div
      ref={gameContainer}
      className="App"
      tabIndex={0}
      style={{ position: 'relative', height: '500px', width: '500px', border: '1px solid' }}
    >
      <RoadAsset />
      <CarAsset position={carPosition} />
      <CarAsset position={goldPosition} />
      {!gameStart && (
        <button style={{ position: 'absolute', zIndex: 2 }} onClick={handleGameStart}>
          Start Game
        </button>
      )}
      {gameStart && (
        <p>
          Score: {score}
        </p>
      )}
    </div>
  );
}

export default CarGame;
