import React, { useState, useEffect } from "react";
import {RoadAsset, CarAsset} from './Assets';
//import '../App.css';

const CarGame = () => {
  const [gameStart, setGameStart] = useState(false);
  const [game, setGame] = useState({startGame: false, direction: 'right'});
  const [score, setScore] = useState(0);
  const [carPosition, setCarPosition] = useState({x: 50, y: 50});
  const [goldPosition, setGoldPosition] = useState({x: 200, y: 200});

  useEffect(() => {
    function handleArrowKey(event) {
      if([37, 38, 39, 40].includes(event.keyCode)) {
        event.preventDefault();
        let newPos = {...carPosition};
        let newDirection = game.direction;

        switch(event.keyCode) {
          case 37: // left arrow key
            newPos.x = carPosition.x > 10 ? carPosition.x - 10 : 0;
            newDirection = 'left';
            break;
          case 39: // right arrow key
            newPos.x = carPosition.x < 440 ? carPosition.x + 10 : 450;
            newDirection = 'right';
            break;
          case 38: // up arrow key
            newPos.y = carPosition.y > 10 ? carPosition.y - 10 : 0;
            newDirection = 'up';
            break;
          case 40: // down arrow key
            newPos.y = carPosition.y < 440 ? carPosition.y + 10 : 450;
            newDirection = 'down';
            break;
        }

        setGame({startGame: game.startGame, direction: newDirection});
        setCarPosition(newPos);
      }
    }

    game.startGame && window.addEventListener('keydown', handleArrowKey);

    return () => window.removeEventListener('keydown', handleArrowKey);
  }, [carPosition, game, game.startGame]);

  useEffect(() => {
    if(Math.abs(carPosition.x - goldPosition.x) < 10 && Math.abs(carPosition.y - goldPosition.y) < 10) {
      setScore(score => score + 1);
      setGoldPosition({
        x: Math.random() * 450,
        y: Math.random() * 450
      });
    }
  }, [carPosition]);

  const handleGameStart = () => {
        setGameStart(true);
    };  

  return (
        <div className="App" style={{position: 'relative', height: '500px', width: '500px', border: '1px solid'}}>
            <RoadAsset />
            <CarAsset position={carPosition} />
            {!gameStart && (
                <button style={{position: 'absolute', zIndex: 2}} onClick={handleGameStart}>
                    Start Game
                </button>
            )}
        </div>
    );
}

export default CarGame;
